function collapsible(e,t,l){const s=document.getElementById(e).getElementsByClassName(t),i=document.getElementById(e).getElementsByClassName(l);for(let e=0;e<s.length;e++)s[e].addEventListener("click",function(){for(let e=0;e<i.length;e++)i[e].style.height="0px";if(this.classList.contains("active"))this.classList.remove("active");else{for(let e=0;e<s.length;e++)s[e].classList.remove("active");this.classList.toggle("active");var e=this.nextElementSibling;e.style.height=e.scrollHeight+"px"}})}collapsible("commands","collapsible","content");